mapper.select:
select * from yssp_activity where activity_status = 1
and activity_id = #{activityId}
order by acitvity_created_time DESC

select

,

from yssp_activity
where activity_id = #{activityId,jdbcType=BIGINT}
and activity_interactive_id = #{activityInteractiveId,jdbcType=BIGINT}

mapper.delete:
delete from yssp_activity
where activity_id = #{activityId,jdbcType=BIGINT}
and activity_interactive_id = #{activityInteractiveId,jdbcType=BIGINT}

mapper.update:
update yssp_activity
set activity_status = -1
where activity_id = #{id}

UPDATE yssp_activity
SET 
activity_join_number = activity_join_number + #{activityJoinNumber,jdbcType=BIGINT},
activity_max_number = activity_max_number - #{activityJoinNumber,jdbcType=BIGINT},
activity_update_time = #{activityUpdateTime,jdbcType=VARCHAR}
WHERE activity_id = #{activityId,jdbcType=BIGINT}
