mapper.select:
SELECT
FROM yssp_share WHERE share_status != -1
AND share_to_userid = #{shareToUserid,jdbcType=BIGINT}
AND share_object_id = #{shareObjectId,jdbcType=BIGINT}
AND share_object_type = #{shareObjectType,jdbcType=TINYINT}
AND share_userid = #{shareUserid,jdbcType=BIGINT}
GROUP BY share_userid
